var React = require('react');

export const Transaction = ( {transaction})=> {
	const sign= transaction.amount < 0 ? '-' :'+';
	const color= transaction.amount < 0 ? 'minus' :'plus';


	
		return (
			<h2>
			<li className={color}>
		          { transaction.text } <span> {sign}${Math.abs( transaction.amount) }</span><button className="delete-btn">x</button>
		        </li>

			</h2>
			
		)
	
}

//we can use props and do props.transaction.text and it will work 
